import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Correct import

function BookingsHome() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token); // Decode the token
        setEmail(decoded.email); // Set the email from the token
      } catch (error) {
        console.error('Invalid token:', error);
      }
    }
  }, []);

  const handleBookAppointment = () => {
    navigate('/schedule');
  };

  const handleManageAppointments = () => {
    navigate('/manage-appointments');
  };

  return (
    <div style={{ maxWidth: '400px', margin: '50px auto', textAlign: 'center' }}>
      <h2>Welcome! {email ? `Logged in as: ${email}` : 'Guest'}</h2>
      <button style={{ margin: '10px' }} onClick={handleBookAppointment}>
        Book an Appointment
      </button>
      <button style={{ margin: '10px' }} onClick={handleManageAppointments}>
        Manage My Appointments
      </button>
    </div>
  );
}

export default BookingsHome;
