import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function AdminPanel() {
  const [appointments, setAppointments] = useState([]);
  const [services, setServices] = useState([]);
  const [providerId, setProviderId] = useState(1);
  const [serviceName, setServiceName] = useState('');
  const [duration, setDuration] = useState('');
  const [price, setPrice] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const queryParams = new URLSearchParams(window.location.search);
    const token = queryParams.get('token') || localStorage.getItem('token');

    if (token) {
      axios
        .post('http://localhost:5000/api/verifyToken', { token })
        .then((response) => {
          if (response.data.valid) {
            localStorage.setItem('token', token);
            fetchAppointmentsAndServices(token);
          } else {
            navigate('/login');
          }
        })
        .catch(() => navigate('/login'));
    } else {
      navigate('/login');
    }
  }, [navigate]);

  const fetchAppointmentsAndServices = (token) => {
    axios
      .get('http://localhost:5000/bookings/admin/all', {
        headers: { Authorization: `Bearer ${token}` },  // Fixed template string
      })
      .then((response) => setAppointments(response.data))
      .catch((error) => {
        console.error(error);
        alert('Failed to fetch appointments');
      });

    axios
      .get('http://localhost:5000/services/admin', {
        headers: { Authorization: `Bearer ${token}` },  // Fixed template string
      })
      .then((res) => setServices(res.data))
      .catch((err) => {
        console.error(err);
        alert('Failed to fetch services');
      });
  };

  return (
    <div style={{ maxWidth: '800px', margin: '50px auto' }}>
      <h1>Admin Panel</h1>
      <h2>All Appointments</h2>
      <table>
        <thead>
          <tr>
            <th>Booking ID</th>
            <th>User Name</th>
            <th>User Email</th>
            <th>Service</th>
            <th>Date</th>
            <th>Time</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map((appt) => (
            <tr key={appt.booking_id}>
              <td>{appt.booking_id}</td>
              <td>{appt.user_name}</td>
              <td>{appt.user_email}</td>
              <td>{appt.service_name}</td>
              <td>{appt.date}</td>
              <td>{appt.time}</td>
              <td>{appt.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminPanel;
