import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AppointmentScheduler.css';

function AppointmentScheduler() {
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState('');
  const [date, setDate] = useState('');
  const [availableSlots, setAvailableSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/services')
      .then((res) => {
        setServices(res.data);
      })
      .catch((err) => {
        console.error('Failed to fetch services:', err);
      });
  }, []);

  const handleCheckAvailability = () => {
    if (!selectedService || !date) {
      alert('Please select a service and date.');
      return;
    }
    axios.get(`http://localhost:5000/availability?provider_id=1&service_id=${selectedService}&date=${date}`)
      .then((res) => {
        setAvailableSlots(res.data);
      })
      .catch((err) => {
        console.error('Error fetching availability:', err);
      });
  };

  const handleBookAppointment = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login first!');
      return;
    }

    axios.post('http://localhost:5000/bookings', {
      provider_id: 1,
      service_id: selectedService,
      date: date,
      time: selectedSlot
    }, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(() => {
      setSuccessMessage('Appointment booked successfully!');
      setTimeout(() => {
        navigate('/bookings-home');
      }, 2000);
    })
    .catch((err) => {
      console.error('Failed to book appointment:', err);
      alert('Failed to book appointment');
    });
  };

  return (
    <div className="appointment-container">
      <h1 className="appointment-title">Book an Appointment</h1>
      <div className="form-group">
        <label className="form-label">Service:</label>
        <select
          className="form-input"
          onChange={(e) => setSelectedService(e.target.value)}
        >
          <option value="">-- Select Service --</option>
          {services.map((s) => (
            <option key={s.id} value={s.id}>
              {s.name} ({s.duration} min) - {s.price} AED
            </option>
          ))}
        </select>
      </div>
      <div className="form-group">
        <label className="form-label">Date:</label>
        <input
          type="date"
          className="form-input"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
      </div>
      <button
        className="btn btn-check"
        onClick={handleCheckAvailability}
      >
        Check Availability
      </button>
      {availableSlots.length > 0 && (
        <div className="form-group">
          <label className="form-label">Available Time Slots:</label>
          <select
            className="form-input"
            onChange={(e) => setSelectedSlot(e.target.value)}
          >
            <option value="">-- Select Time --</option>
            {availableSlots.map((slot, index) => (
              <option key={index} value={slot}>{slot}</option>
            ))}
          </select>
        </div>
      )}
      {selectedSlot && (
        <button
          className="btn btn-book"
          onClick={handleBookAppointment}
        >
          Book Now
        </button>
      )}
      {successMessage && (
        <p className="success-message">{successMessage}</p>
      )}
    </div>
  );
}

export default AppointmentScheduler;
