import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AppointmentScheduler() {
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState(null);
  const [date, setDate] = useState('');
  const [availableSlots, setAvailableSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState('');

  useEffect(() => {
    // Fetch services on mount
    axios.get('http://localhost:5000/services?provider_id=1')
      .then(res => setServices(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleCheckAvailability = () => {
    if (!selectedService || !date) return;
    axios.get(`http://localhost:5000/availability?provider_id=1&service_id=${selectedService}&date=${date}`)
      .then(res => setAvailableSlots(res.data))
      .catch(err => console.error(err));
  }

  const handleBookAppointment = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login first!');
      return;
    }

    axios.post('http://localhost:5000/bookings', {
      provider_id: 1,
      service_id: selectedService,
      date: date,
      time: selectedSlot
    }, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(res => {
      alert('Appointment booked successfully!');
    })
    .catch(err => {
      console.error(err);
      alert('Failed to book appointment');
    });
  }

  return (
    <div style={{maxWidth:'400px',margin:'50px auto'}}>
      <h2>Book an Appointment</h2>
      <div>
        <label>Service:</label><br/>
        <select onChange={e => setSelectedService(e.target.value)}>
          <option value="">-- Select Service --</option>
          {services.map(s => (
            <option key={s.id} value={s.id}>{s.name} ({s.duration} min)</option>
          ))}
        </select>
      </div>
      <div style={{marginTop:'10px'}}>
        <label>Date:</label><br/>
        <input type="date" value={date} onChange={e => setDate(e.target.value)} required/>
      </div>
      <button onClick={handleCheckAvailability} style={{marginTop:'10px'}}>Check Availability</button>
      {availableSlots.length > 0 && (
        <div style={{marginTop:'10px'}}>
          <label>Available Time Slots:</label><br/>
          <select onChange={e => setSelectedSlot(e.target.value)}>
            <option value="">-- Select Time --</option>
            {availableSlots.map((slot, index) => (
              <option key={index} value={slot}>{slot}</option>
            ))}
          </select>
        </div>
      )}
      {selectedSlot && (
        <button onClick={handleBookAppointment} style={{marginTop:'10px'}}>Book Now</button>
      )}
    </div>
  );
}

export default AppointmentScheduler;
