import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

function BookingsHome() {
  const navigate = useNavigate();
  const [userEmail, setUserEmail] = useState('');

  useEffect(() => {
    // Get token from localStorage
    const token = localStorage.getItem('token');
    if (token) {
      const decoded = jwtDecode(token);  // Decode token
      setUserEmail(decoded.email);       // Set email from token
    }
  }, []);

  const handleBookAppointment = () => {
    navigate('/schedule');
  };

  const handleManageAppointments = () => {
    navigate('/manage-appointments');
  };

  return (
    <div style={{ maxWidth: '400px', margin: '50px auto', textAlign: 'center' }}>
      <h2>Welcome, {userEmail || 'Guest'}!</h2>
      <p>You are logged in as: <strong>{userEmail || 'Not Available'}</strong></p>
      
      <button style={{ margin: '10px' }} onClick={handleBookAppointment}>
        Book an Appointment
      </button>
      <button style={{ margin: '10px' }} onClick={handleManageAppointments}>
        Manage My Appointments
      </button>
    </div>
  );
}

export default BookingsHome;
