import React, { useEffect, useState } from 'react';
import { getUserBookings, updateBooking, deleteBooking } from '../services/bookingService';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function ManageAppointments() {
  const [bookings, setBookings] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [newDate, setNewDate] = useState('');
  const [availableSlots, setAvailableSlots] = useState([]);
  const [newTime, setNewTime] = useState('');

  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      alert('Please login first!');
      navigate('/login');
      return;
    }

    getUserBookings(token).then(setBookings).catch(console.error);
  }, [token, navigate]);

  const handleEdit = (booking) => {
    setEditingId(booking.id);
    setNewDate(booking.date); // Pre-fill current date
    setNewTime(booking.time.slice(0, 5)); // Pre-fill current time
    setAvailableSlots([]); // Clear previous slots
  };

  const handleDateChange = async (e, booking) => {
    const selectedDate = e.target.value;
    setNewDate(selectedDate);

    // Fetch available time slots for the selected date
    try {
      const response = await axios.get(
        `http://localhost:5000/availability?provider_id=${booking.provider_id}&service_id=${booking.service_id}&date=${selectedDate}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAvailableSlots(response.data); // Update available slots
      setNewTime(''); // Reset selected time
    } catch (err) {
      console.error(err);
      alert('Failed to fetch available time slots.');
    }
  };

  const handleUpdate = async () => {
    try {
      await updateBooking(editingId, newDate, newTime, token);
      alert('Appointment rescheduled successfully!');
      const updatedBookings = await getUserBookings(token);
      setBookings(updatedBookings);
      setEditingId(null);
      setAvailableSlots([]);
    } catch (err) {
      console.error(err);
      alert('Failed to reschedule appointment.');
    }
  };

  const handleCancel = async (id) => {
    if (window.confirm('Are you sure you want to cancel this appointment?')) {
      try {
        await deleteBooking(id, token);
        alert('Appointment canceled successfully!');
        const updatedBookings = await getUserBookings(token);
        setBookings(updatedBookings);
      } catch (err) {
        console.error(err);
        alert('Failed to cancel appointment.');
      }
    }
  };

  return (
    <div style={{ maxWidth: '500px', margin: '50px auto' }}>
      <h2>My Appointments</h2>
      {bookings.length === 0 && <p>You have no upcoming appointments.</p>}
      {bookings.map((booking) => (
        <div key={booking.id} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
          {editingId === booking.id ? (
            <div>
              <p>Service: {booking.service_name}</p>
              <p>Current Date: {booking.date}</p>
              <p>Current Time: {booking.time}</p>
              <label>New Date:</label>
              <input
                type="date"
                value={newDate}
                onChange={(e) => handleDateChange(e, booking)}
              />
              {availableSlots.length > 0 && (
                <>
                  <label>New Time:</label>
                  <select value={newTime} onChange={(e) => setNewTime(e.target.value)}>
                    <option value="">-- Select Time --</option>
                    {availableSlots.map((slot, index) => (
                      <option key={index} value={slot}>
                        {slot}
                      </option>
                    ))}
                  </select>
                </>
              )}
              <button
                onClick={handleUpdate}
                disabled={!newDate || !newTime}
                style={{ marginTop: '10px' }}
              >
                Save
              </button>
              <button
                onClick={() => {
                  setEditingId(null);
                  setAvailableSlots([]);
                }}
                style={{ marginLeft: '10px', marginTop: '10px' }}
              >
                Cancel
              </button>
            </div>
          ) : (
            <div>
              <p>Service: {booking.service_name}</p>
              <p>Date: {booking.date}</p>
              <p>Time: {booking.time}</p>
              <p>Status: {booking.status}</p>
              <button onClick={() => handleEdit(booking)}>Reschedule</button>
              <button onClick={() => handleCancel(booking.id)}>Cancel</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default ManageAppointments;
