import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './AppointmentScheduler.css';

function AppointmentScheduler() {
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState(null);
  const [date, setDate] = useState('');
  const [availableSlots, setAvailableSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState('');
  const [successMessage, setSuccessMessage] = useState(''); // State for success message
  const navigate = useNavigate(); // Initialize useNavigate

  useEffect(() => {
    // Fetch services on mount
    axios.get('http://localhost:5000/services?provider_id=1')
      .then(res => setServices(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleCheckAvailability = () => {
    if (!selectedService || !date) return;
    axios.get(`http://localhost:5000/availability?provider_id=1&service_id=${selectedService}&date=${date}`)
      .then(res => setAvailableSlots(res.data))
      .catch(err => console.error(err));
  };

  const handleBookAppointment = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login first!');
      return;
    }

    axios.post('http://localhost:5000/bookings', {
      provider_id: 1,
      service_id: selectedService,
      date: date,
      time: selectedSlot
    }, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(() => {
      setSuccessMessage('Appointment booked successfully!'); // Set success message
      setTimeout(() => {
        navigate('/bookings-home'); // Navigate after 2 seconds
      }, 2000); // Delay navigation for 2 seconds to display the message
    })
    .catch(err => {
      console.error(err);
      alert('Failed to book appointment');
    });
  };

  return (
    <div className="appointment-container">
      <h1 className="appointment-title">Book an Appointment</h1>
      <div className="form-group">
        <label className="form-label">Service:</label>
        <select
          className="form-input"
          onChange={e => setSelectedService(e.target.value)}>
          <option value="">-- Select Service --</option>
          {services.map(s => (
            <option key={s.id} value={s.id}>
              {s.name} ({s.duration} min) - {s.price} AED
            </option>
          ))}
        </select>
      </div>
      <div className="form-group">
        <label className="form-label">Date:</label>
        <input
          type="date"
          className="form-input"
          value={date}
          onChange={e => setDate(e.target.value)}
          required
        />
      </div>
      <button
        className="btn btn-check"
        onClick={handleCheckAvailability}
      >
        Check Availability
      </button>
      {availableSlots.length > 0 && (
        <div className="form-group">
          <label className="form-label">Available Time Slots:</label>
          <select
            className="form-input"
            onChange={e => setSelectedSlot(e.target.value)}
          >
            <option value="">-- Select Time --</option>
            {availableSlots.map((slot, index) => (
              <option key={index} value={slot}>{slot}</option>
            ))}
          </select>
        </div>
      )}
      {selectedSlot && (
        <button
          className="btn btn-book"
          onClick={handleBookAppointment}
        >
          Book Now
        </button>
      )}
      {successMessage && (
        <p className="success-message">{successMessage}</p> // Display success message
      )}
    </div>
  );
}

export default AppointmentScheduler;
