import React, { useEffect, useState } from 'react';
import { getUserBookings, updateBooking, deleteBooking } from '../services/bookingService';
import { useNavigate } from 'react-router-dom';

function ManageAppointments() {
  const [bookings, setBookings] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [newDate, setNewDate] = useState('');
  const [newTime, setNewTime] = useState('');

  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      alert('Please login first!');
      navigate('/login'); // Use navigate instead of history.push
      return;
    }

    // Fetch user bookings
    getUserBookings(token)
      .then(setBookings)
      .catch(console.error);
  }, [token, navigate]);

  const handleEdit = (booking) => {
    setEditingId(booking.id);
    setNewDate(booking.date);
    setNewTime(booking.time.slice(0,5)); // Format "HH:MM"
  };

  const handleUpdate = async () => {
    await updateBooking(editingId, newDate, newTime, token);
    // Refresh bookings
    const updated = await getUserBookings(token);
    setBookings(updated);
    setEditingId(null);
  };

  const handleCancel = async (id) => {
    if (window.confirm('Are you sure you want to cancel this appointment?')) {
      await deleteBooking(id, token);
      const updated = await getUserBookings(token);
      setBookings(updated);
    }
  };

  return (
    <div style={{maxWidth:'500px', margin:'50px auto'}}>
      <h2>My Appointments</h2>
      {bookings.length === 0 && <p>You have no upcoming appointments.</p>}
      {bookings.map(b => (
        <div key={b.id} style={{border:'1px solid #ccc', padding:'10px', marginBottom:'10px'}}>
          {editingId === b.id ? (
            <div>
              <p>Service: {b.service_name}</p>
              <label>New Date: </label>
              <input type="date" value={newDate} onChange={e => setNewDate(e.target.value)} />
              <label>New Time: </label>
              <input type="time" value={newTime} onChange={e => setNewTime(e.target.value)} />
              <button onClick={handleUpdate}>Save</button>
              <button onClick={() => setEditingId(null)}>Cancel</button>
            </div>
          ) : (
            <div>
              <p>Service: {b.service_name}</p>
              <p>Date: {b.date}</p>
              <p>Time: {b.time}</p>
              <p>Status: {b.status}</p>
              <button onClick={() => handleEdit(b)}>Reschedule</button>
              <button onClick={() => handleCancel(b.id)}>Cancel</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default ManageAppointments;
