import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUserBookings } from '../services/bookingService'; // Ensure this file/path matches your setup

function BookingsHome() {
  const navigate = useNavigate();
  const [hasBookings, setHasBookings] = useState(false);
  const token = localStorage.getItem('token');

  useEffect(() => {
    // If user not logged in, redirect or handle accordingly
    if (!token) {
      // For example, redirect to login
      navigate('/login');
      return;
    }

    // Fetch user bookings
    getUserBookings(token)
      .then(bookings => {
        if (bookings && bookings.length > 0) {
          setHasBookings(true);
        } else {
          setHasBookings(false);
        }
      })
      .catch(err => {
        console.error(err);
        setHasBookings(false);
      });
  }, [token, navigate]);

  const handleBookAppointment = () => {
    navigate('/schedule');
  };

  const handleManageAppointments = () => {
    navigate('/manage-appointments');
  };

  return (
    <div style={{maxWidth:'400px', margin:'50px auto', textAlign:'center'}}>
      <h2>Welcome! What would you like to do?</h2>
      <button style={{margin:'10px'}} onClick={handleBookAppointment}>Book an Appointment</button>
      {hasBookings && (
        <button style={{margin:'10px'}} onClick={handleManageAppointments}>Manage My Appointments</button>
      )}
    </div>
  );
}

export default BookingsHome;
