import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function AdminPanel() {
  const [appointments, setAppointments] = useState([]);
  const [services, setServices] = useState([]);
  const [providerId, setProviderId] = useState(1);
  const [serviceName, setServiceName] = useState('');
  const [duration, setDuration] = useState('');
  const [price, setPrice] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Attempt to get the token from the URL query param or from localStorage
    const queryParams = new URLSearchParams(window.location.search);
    const tokenFromQuery = queryParams.get('token');
    const token = tokenFromQuery || localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    // Verify the token with the backend
    axios
      .post('http://localhost:5000/api/verifyToken', { token })
      .then(response => {
        if (response.data.valid) {
          // If valid, store token locally so subsequent refreshes work
          localStorage.setItem('token', token);
          fetchAppointmentsAndServices(token);
        } else {
          navigate('/login');
        }
      })
      .catch(() => {
        navigate('/login');
      });
  }, [navigate]);

  const fetchAppointmentsAndServices = async (token) => {
    try {
      // Fetch appointments
      const apptsRes = await axios.get(
        'http://localhost:5000/bookings/admin/all',
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAppointments(apptsRes.data);

      // Fetch services
      const servRes = await axios.get(
        'http://localhost:5000/services/admin',
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setServices(servRes.data);
    } catch (error) {
      console.error('Error fetching data', error);
      alert('Failed to fetch appointments or services');
    }
  };

  const handleCreateService = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token. Please log in again.');
      navigate('/login');
      return;
    }

    if (!serviceName || !duration || !price) {
      alert('Please fill all service fields');
      return;
    }

    try {
      // Create service
      const createRes = await axios.post(
        'http://localhost:5000/services/admin',
        {
          provider_id: providerId,
          name: serviceName,
          duration,
          price,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert(createRes.data.message || 'Service created');

      // Refresh the services list
      const servRes = await axios.get('http://localhost:5000/services/admin', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setServices(servRes.data);

      // Reset form fields
      setServiceName('');
      setDuration('');
      setPrice('');
    } catch (err) {
      console.error(err);
      alert('Failed to create service');
    }
  };

  return (
    <div style={{ maxWidth: '800px', margin: '50px auto' }}>
      <h1>Admin Panel</h1>

      <h2>All Appointments</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Booking ID</th>
            <th>User Name</th>
            <th>User Email</th>
            <th>Service</th>
            <th>Date</th>
            <th>Time</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map((appt) => (
            <tr key={appt.booking_id}>
              <td>{appt.booking_id}</td>
              <td>{appt.user_name}</td>
              <td>{appt.user_email}</td>
              <td>{appt.service_name}</td>
              <td>{appt.date}</td>
              <td>{appt.time}</td>
              <td>{appt.status}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2 style={{ marginTop: '40px' }}>Manage Services</h2>
      <h3>Create New Service</h3>
      <div style={{ marginBottom: '20px' }}>
        <div>
          <label>Provider ID: </label>
          <input
            type="number"
            value={providerId}
            onChange={(e) => setProviderId(e.target.value)}
          />
        </div>
        <div>
          <label>Service Name: </label>
          <input
            type="text"
            value={serviceName}
            onChange={(e) => setServiceName(e.target.value)}
          />
        </div>
        <div>
          <label>Duration (minutes): </label>
          <input
            type="number"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
        </div>
        <div>
          <label>Price (AED): </label>
          <input
            type="number"
            step="0.01"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
        <button onClick={handleCreateService}>Create Service</button>
      </div>

      <h3>Existing Services</h3>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Service ID</th>
            <th>Provider ID</th>
            <th>Name</th>
            <th>Duration</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {services.map((service) => (
            <tr key={service.id}>
              <td>{service.id}</td>
              <td>{service.provider_id}</td>
              <td>{service.name}</td>
              <td>{service.duration}</td>
              <td>{service.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminPanel;
