import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AppointmentScheduler.css';

function AppointmentScheduler() {
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState(null);
  const [date, setDate] = useState('');
  const [availableSlots, setAvailableSlots] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null); // Index of the selected slot

  useEffect(() => {
    // Fetch services on mount
    axios.get('http://localhost:5000/services?provider_id=1')
      .then(res => setServices(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleCheckAvailability = () => {
    if (!selectedService || !date) return;
    axios.get(`http://localhost:5000/availability?provider_id=1&service_id=${selectedService}&date=${date}`)
      .then(res => {
        setAvailableSlots(res.data);
        setSelectedIndex(null); // reset selected index if previously set
      })
      .catch(err => console.error(err));
  };

  const handleBookAppointment = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login first!');
      return;
    }

    if (selectedIndex === null) {
      alert('Please select a time slot first.');
      return;
    }

    const selectedSlot = availableSlots[selectedIndex];
    axios.post('http://localhost:5000/bookings', {
      provider_id: 1,
      service_id: selectedService,
      date: date,
      time: selectedSlot
    }, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(() => {
      alert('Appointment booked successfully!');
    })
    .catch(err => {
      console.error(err);
      alert('Failed to book appointment');
    });
  };

  return (
    <div className="appointment-container">
      <h1 className="appointment-title">Book an Appointment</h1>
      <div className="form-group">
        <label className="form-label">Service:</label>
        <select
          className="form-input"
          onChange={e => setSelectedService(e.target.value)}>
          <option value="">-- Select Service --</option>
          {services.map(s => (
            <option key={s.id} value={s.id}>
              {s.name} ({s.duration} min) - {s.price} AED
            </option>
          ))}
        </select>
      </div>
      <div className="form-group">
        <label className="form-label">Date:</label>
        <input
          type="date"
          className="form-input"
          value={date}
          onChange={e => setDate(e.target.value)}
          required
        />
      </div>
      <button
        className="btn btn-check"
        onClick={handleCheckAvailability}
      >
        Check Availability
      </button>

      {availableSlots.length > 0 && (
        <div className="form-group" style={{marginTop: '20px'}}>
          <label className="form-label">Available Time Slots:</label>
          <p>Select a time using the slider below:</p>
          <input
            type="range"
            min="0"
            max={availableSlots.length - 1}
            value={selectedIndex !== null ? selectedIndex : 0}
            onChange={(e) => setSelectedIndex(parseInt(e.target.value))}
          />
          {selectedIndex !== null && (
            <div style={{marginTop: '10px'}}>
              <strong>Selected Time: </strong>{availableSlots[selectedIndex]}
            </div>
          )}
        </div>
      )}

      {selectedIndex !== null && (
        <button
          className="btn btn-book"
          style={{marginTop: '20px'}}
          onClick={handleBookAppointment}
        >
          Book Now
        </button>
      )}
    </div>
  );
}

export default AppointmentScheduler;
