import React, { useEffect, useState } from 'react';
import { getUserBookings, updateBooking, deleteBooking } from '../services/bookingService';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function ManageAppointments() {
  const [bookings, setBookings] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [availableDays, setAvailableDays] = useState([]);
  const [availableTimes, setAvailableTimes] = useState([]);
  const [newDate, setNewDate] = useState('');
  const [newTime, setNewTime] = useState('');

  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      alert('Please login first!');
      navigate('/login');
      return;
    }

    getUserBookings(token).then(setBookings).catch(console.error);
  }, [token, navigate]);

  const handleEdit = async (booking) => {
    setEditingId(booking.id);
    setNewDate('');
    setNewTime('');
    setAvailableTimes([]);

    // Fetch available days
    // If you have a dedicated endpoint for available days, call that here.
    // If currently the `availability` endpoint returns a list of possible days when no date is provided,
    // you can do something like:
    try {
      const response = await axios.get(
        `http://localhost:5000/availability?provider_id=${booking.provider_id}&service_id=${booking.service_id}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAvailableDays(response.data); // This should be a list of dates if that's how you've set it up
    } catch (err) {
      console.error(err);
      alert('Failed to fetch available days.');
    }
  };

  // Once a date is chosen, fetch times for that specific date
  const handleDateChange = async (selectedDate) => {
    setNewDate(selectedDate);
    setNewTime(''); // Reset time
    try {
      const response = await axios.get(
        `http://localhost:5000/availability?provider_id=${bookings.find(b => b.id === editingId).provider_id}&service_id=${bookings.find(b => b.id === editingId).service_id}&date=${selectedDate}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAvailableTimes(response.data); // This should be the list of available time slots for that day
    } catch (err) {
      console.error(err);
      alert('Failed to fetch available times.');
    }
  };

  const handleUpdate = async () => {
    if (!newDate || !newTime) {
      alert('Please select both a new date and a new time.');
      return;
    }
    try {
      await updateBooking(editingId, newDate, newTime, token);
      alert('Appointment rescheduled successfully!');
      const updatedBookings = await getUserBookings(token);
      setBookings(updatedBookings);
      setEditingId(null);
      setAvailableDays([]);
      setAvailableTimes([]);
    } catch (err) {
      console.error(err);
      alert('Failed to reschedule appointment.');
    }
  };

  const handleCancel = async (id) => {
    if (window.confirm('Are you sure you want to cancel this appointment?')) {
      try {
        await deleteBooking(id, token);
        alert('Appointment canceled successfully!');
        const updatedBookings = await getUserBookings(token);
        setBookings(updatedBookings);
      } catch (err) {
        console.error(err);
        alert('Failed to cancel appointment.');
      }
    }
  };

  return (
    <div style={{ maxWidth: '500px', margin: '50px auto' }}>
      <h2>My Appointments</h2>
      {bookings.length === 0 && <p>You have no upcoming appointments.</p>}
      {bookings.map((booking) => (
        <div key={booking.id} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
          {editingId === booking.id ? (
            <div>
              <p>Service: {booking.service_name}</p>
              <p>Current Date: {booking.date}</p>
              <p>Current Time: {booking.time}</p>
              <label>New Date:</label>
              {availableDays.length > 0 ? (
                <select value={newDate} onChange={(e) => handleDateChange(e.target.value)}>
                  <option value="">-- Select New Date --</option>
                  {availableDays.map((day, index) => (
                    <option key={index} value={day}>
                      {day}
                    </option>
                  ))}
                </select>
              ) : (
                <p>Loading available days...</p>
              )}

              {newDate && availableTimes.length > 0 && (
                <>
                  <label>New Time:</label>
                  <select value={newTime} onChange={(e) => setNewTime(e.target.value)}>
                    <option value="">-- Select New Time --</option>
                    {availableTimes.map((time, index) => (
                      <option key={index} value={time}>
                        {time}
                      </option>
                    ))}
                  </select>
                </>
              )}

              <button
                onClick={handleUpdate}
                disabled={!newDate || !newTime}
                style={{ marginTop: '10px' }}
              >
                Save
              </button>
              <button
                onClick={() => {
                  setEditingId(null);
                  setAvailableDays([]);
                  setAvailableTimes([]);
                }}
                style={{ marginLeft: '10px', marginTop: '10px' }}
              >
                Cancel
              </button>
            </div>
          ) : (
            <div>
              <p>Service: {booking.service_name}</p>
              <p>Date: {booking.date}</p>
              <p>Time: {booking.time}</p>
              <p>Status: {booking.status}</p>
              <button onClick={() => handleEdit(booking)}>Reschedule</button>
              <button onClick={() => handleCancel(booking.id)}>Cancel</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default ManageAppointments;
